import React from "react";
import SimpleCard from "./components/Cards";
import "./App.css";
import Dropdown from "./components/Dropdown";
import { getSummary } from "./api/covidApi";

function App() {
  return (
    <>
      <div className="container-header">
        <h1>Covid-19 Tracker</h1>
      </div>
      <div className="container-1">
        <div className="box-1">
          <SimpleCard
            name="TotalDeaths"
            text="Confirmed"
            onChange={getSummary("TotalDeaths")}
          />
        </div>
        <div className="box-2">
          <SimpleCard text="Recovered " />
        </div>
        <div className="box-2">
          <SimpleCard text="Deaths" />
        </div>
      </div>
      <div className="container-2">
        <Dropdown />
      </div>
    </>
  );
}

export default App;
