import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import { fetchData } from "../api/covidApi";

function BarChart(confirmed, recovered, deaths) {
  const [dailyRate, setDailyRate] = useState({});

  useEffect(() => {});

  const data = {
    labels: dailyRate.days,
    datasets: [
      {
        label: "Confirmed",
        data: dailyRate.confirmed,
        fill: false,
        pointStyle: "line",
        borderColor: "blue",
      },
      {
        label: "Deaths",
        data: dailyRate.deaths,
        borderColor: "#FF0000",
        fill: "false",
        borderWidth: 0.5,
      },
    ],
  };

  return <Bar data={data}></Bar>;
}

export default BarChart;
