const fetch = require("node-fetch");

function getSummary(x) {
  fetch("https://api.covid19api.com/summary")
    .then((res) => res.json())
    .then((data) => {
      return data.Global[x];
    });
}

let totalDeaths = getSummary("TotalDeaths");

console.log(totalDeaths);
